mapper.select:
select
  iid, uid, content, addtime, photo, vidio, type_id, state
from information


        and iid = #{iid}


        and uid = #{uid}


        and content = #{content}


        and addtime = #{addtime}


        and photo = #{photo}


        and vidio = #{vidio}


        and type_id = #{typeId}


        and state = #{state}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from information


        and iid = #{iid}


        and uid = #{uid}


        and content = #{content}


        and addtime = #{addtime}


        and photo = #{photo}


        and vidio = #{vidio}


        and type_id = #{typeId}


        and state = #{state}
